import axios from "axios";
const Post = ({ comments }) => {
  console.log(comments);
  return <h1>You clicked on Post </h1>;
};

Post.getInitialProps = async ({ query }) => {
  const res = axios.get(
    "https://jsonplaceholder.typicode.com/comments?postId=" + query.id
  );
  const comments = res.data;
  console.log(res);
  return { comments };
};
export default Post;
